import './ExpenseItem.css'

function ExpenseItem(props) {
// function ExpenseItem({title, amount, date}) { // destructuring option

    const month = props.expense.date.toLocaleString('en-US', {month: 'long'})
    const yaer = props.expense.date.toLocaleString('en-US', {yaer: 'long'})
    const day = props.expense.date.toLocaleString('en-US', {day: '2-digit'})

    return (
        <div className="expense-item">
            <div>
                <div className='expense-date-month'>{month} </div>
                <div className='expense-date-year'>{year}</div>
                <div className='expense-date-day'>{day}</div>
            </div>
            <div className="expense-item__description">
                <h2>{props.expense.title}</h2>
                <div className="expense-item__price">${props.expense.amount}</div>
            </div>
        </div>
    )
};

export default ExpenseItem;
